import { React, useEffect, useState } from "react";
import Buzzes from "../components/Buzzes";
import { useNavigate } from "react-router-dom";
import { useBuzzContext } from "../hooks/useBuzzContext";
import { getByUser } from "../api/requests";

const Posts = () => {
  const [error, setError] = useState("");
  const { buzz, dispatch: dispatchBuzz } = useBuzzContext();
  const [data, setData] = useState(null);
  const [title, setTitle] = useState("My Buzzes");
  const navigate = useNavigate();
  const id = JSON.parse(localStorage.getItem("user"))["username"];
  function handleClick(id) {
    navigate("/buzz/" + id);
  }
  useEffect(() => {
    let reqData = {
      id: id,
    };
    setError("");
    const response = (data) => {
      if (data["error"]) {
        setError(data["error"].message);
      } else {
        console.log(data);
        setData(data);
        dispatchBuzz({ type: "SET_BUZZ", payload: data["buzz"] });
      }
    };
    getByUser(reqData, response);
  }, [id, dispatchBuzz]);
  return (
    <div className="ml-64">
      {buzz && (
        <h1 className="mb-4 text-4xl mt-5 text-center font-extrabold leading-none tracking-tight text-gray-900 md:text-5xl lg:text-6xl">
          {title}
        </h1>
      )}
      <div class="flex justify-center">
        <button
          onClick={setTitle("My posts")}
          class="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-l"
        >
          My Posts
        </button>
        <button class="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-r">
          My Comments
        </button>
        <button class="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded-r">
          Liked Buzzes
        </button>
      </div>
      {buzz &&
        buzz.map((b) => (
          <Buzzes
            key={b._id}
            onClick={() => {
              handleClick(b._id);
            }}
            buzz={b}
          />
        ))}
      {error && <div className="error">{error}</div>}
    </div>
  );
};

export default Posts;
