import { React, useEffect, useState } from "react";
import Buzzes from "../components/Buzzes";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
import { getFollowing } from "../api/requests";
import { useProfileContext } from "../hooks/useProfileContext";
import { follow } from "../api/requests";

const Following = () => {
  const [error, setError] = useState("");
  const [buzz, setBuzz] = useState(null);
  const { profile, dispatch } = useProfileContext();
  const [followStatus, setFollowStatus] = useState("");
  const { id } = useParams();
  const navigate = useNavigate();



  function handleClick(id) {
    navigate("/buzz/" + id);
  }
  useEffect(() => {
    setError("");
    const response = (data) => {
      if (data["error"]) {
        setError(data["error"].message);
      } else {
        setBuzz(data);
      }
    };
    getFollowing(response);
  }, [id]);

  const handleFollow = async () => {
    let reqData = {
      id: buzz[0].user_id,
    };
    const response = (data) => {
      if (data["error"]) {
        setError(data["error"]);
      } else {
        dispatch({ type: "SET_PROFILE", payload: data["profile"] });
        setFollowStatus(data["action"]);
      }
    };
    await follow(reqData, response);
  };

  return (
    <div className="ml-64">
      {buzz &&
        buzz.map((b) => (
          <Buzzes
            key={b._id}
            onClick={() => {
              handleClick(b._id);
            }}
            buzz={b}
          />
        ))}
      {error && <div className="error">{error}</div>}
    </div>
    //TODO: Make clicking your own buzzes lead to my buzzes and disable following yourself
  );
};

export default Following;
