import React from "react";
import Navigate from "react-router-dom";
import { getById } from "../api/requests";

const Buzzes = ({ buzz }) => {
  

  const handleClick = async (e) => {
    const [error, setError] = useState("");
    let reqData = {
      id: buzz._id
    };
    const response = (data) => {
      if(data["error"]){
        setError(data["error"]);
      }else{
        Navigate('/buzz/',buzz._id)
      }
    };
    e.preventDefault();
    await getById(reqData, response);
  };


  return (
    <div className="">
      <div className="block m-10 rounded-md bg-white p-6 hover:bg-gray-100 shadow-[0_2px_15px_-3px_rgba(0,0,0,0.07),0_10px_20px_-2px_rgba(0,0,0,0.04)]">
        <button>
          <h5 className="mb-2 text-xl font-medium leading-tight text-neutral-800">
            {buzz.name}
          </h5>
          <p className="mb-4 text-base text-neutral-600">{buzz.message}</p>
        </button>
      </div>
    </div>
  );
};

export default Buzzes;
