import { React, useEffect, useState } from "react";
import Buzzes from "../components/Buzzes";
import { useBuzzContext } from "../hooks/useBuzzContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { getByUser } from "../api/requests";
import CreateBuzz from "../components/CreateBuzz";

const Posts = () => {
    const { id } = useParams();
    const [error, setError] = useState("");
    const [buzz, setBuzz] = useState(null);
  
    useEffect(() => {
      let reqData = {
        id: id,
      };
      setError("");
      const response = (data) => {
        if (data["error"]) {
          setError(data["error"]);
        } else {
          getByUser(data);
        }
      };
      getById(reqData, response);
    }, [id]);
  return (
    <div className="ml-64">
      {buzz &&
        buzz.map((b) => (
          <Buzzes
            key={b._id}
            onClick={() => {
              handleClick(b._id);
            }}
            buzz={b}
          />
        ))}
      {error && <div className="error">{error}</div>}

      <CreateBuzz />
    </div>
  );
};

export default Posts;
