//UserModal Component is a popup that shows the following list or followers list of a user

import { React, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getFollowers, getFollowing } from "../api/requests";

const UserModal = (user) => {
  const [userList, setUserList] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleClick = (name) => {
    user.setShowModal(false);
    navigate("/profile/" + name);
  };

  useEffect(() => {
    let reqData = {
      username: user.username,
    };
    if (user.showModal) {
      const response = (data) => {
        if (data["error"]) {
          setError(data["error"]);
        } else {
          setUserList(data);
          //shows a list of users that a profile is followed by or following
          setError("");
        }
      };
      if (user.type === "Followers") {
        getFollowers(reqData, response);
      } else {
        getFollowing(reqData, response);
      }
    }
  }, [user, user.username]);

  return (
    <div>
      {user.showModal ? (
        <>
          <div className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none">
            <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-1/2 bg-white outline-none focus:outline-none">
              <button
                type="button"
                onClick={() => {
                  user.setShowModal(false);
                }}
                className="absolute top-3 right-2.5 text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm w-8 h-8 ml-auto inline-flex justify-center items-center"
              >
                <svg
                  className="w-3 h-3"
                  aria-hidden="true"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 14 14"
                >
                  <path
                    stroke="currentColor"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6"
                  />
                </svg>
                <span className="sr-only">Close modal</span>
              </button>
              <div className="px-6 py-6 lg:px-8">
                <h3 className="mb-4 text-xl font-medium text-gray-900">
                  {user.type}
                </h3>
                <ul className="max-w-md space-y-1 text-gray-500 list-disc list-inside dark:text-gray-400">
                  {userList &&
                    userList.map((user) => (
                      <li
                        key={user}
                        onClick={() => {
                          handleClick(user);
                        }}
                        className="hover:text-blue-500 hover:underline"
                      >
                        {user}
                      </li>
                    ))}
                </ul>
                {error && <div className="error">{error}</div>}
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}
    </div>
  );
};

export default UserModal;
